/**
 * Template Gallery Data
 *
 * 85 pre-built process templates organized by 11 categories.
 * Each template can be imported into an org as a draft template.
 */

export interface GalleryTemplateStep {
  name: string;
  type: 'FORM' | 'QUESTIONNAIRE' | 'FILE_REQUEST' | 'TODO' | 'APPROVAL' | 'ACKNOWLEDGEMENT' | 'ESIGN' | 'DECISION' | 'PDF_FORM' | 'CUSTOM_ACTION' | 'WEB_APP';
  assigneeRole: string;
  sampleFormFields?: Array<{ fieldId: string; label: string; type: string; required?: boolean; options?: Array<{ label: string; value: string }> }>;
  sampleDocumentRef?: string;
  sampleDescription?: string;
}

export interface GalleryTemplate {
  id: string;
  name: string;
  category: string;
  description: string;
  complexity: 'Simple' | 'Standard' | 'Complex';
  tags?: string[];
  trigger: string;
  roles: string[];
  steps: GalleryTemplateStep[];
  setupInstructions?: string;
}

export interface TemplateCategory {
  id: string;
  name: string;
  description: string;
  icon: string;
}

